package projectp1;

public class DatatypeSizes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			System.out.println("Byte size:"+Byte.SIZE+" "+Byte.MIN_VALUE+" "+Byte.MAX_VALUE);
			System.out.println("Short size:"+Short.SIZE+" "+Short.MIN_VALUE+" "+Short.MAX_VALUE);
			System.out.println("Int size:"+Integer.SIZE+" "+Integer.MIN_VALUE+" "+Integer.MAX_VALUE);
			System.out.println("Long size:"+Long.SIZE+" "+Long.MIN_VALUE+" "+Long.MAX_VALUE);
			System.out.println("Double size:"+Double.SIZE+" "+Double.MIN_VALUE+" "+Double.MAX_VALUE);
			System.out.println("Character size:"+Character.SIZE);
			System.out.println("Float size:"+Float.SIZE+" "+Float.MIN_VALUE+" "+Float.MAX_VALUE);
	}

}
