package projectp1;

public class MatrixAddition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[][]= {{10,20},{10,20}};
		int b[][]= {{10,20},{10,20}};
		for(int i=0;i<a.length;i++) {
				for(int j=0;j<a[i].length;j++) {
				System.out.print(a[i][j]+b[i][j]+" ");
			}
				System.out.println();
		}

	}

}
